import { useState, useRef } from "react";
import {useSelector, useDispatch} from "react-redux"
import "./addButtons.css";
import { addService,removeService } from "../../toolkitRedux/sliceToolkit";
export const totalSumm=useSelector(state=> state.toolkit.summ)
export const totalTime=useSelector(state=> state.toolkit.time)

const AddButton = () => {
  const [isActive, setIsActive] = useState(false);
  const knopka = useRef();
  const dispatch=useDispatch()


  console.log("totalSumm: "+totalSumm + "totalTime: "+totalSumm);

  const click = () => {
    setIsActive((prevIsActive) => !prevIsActive);
    if(!isActive){
      dispatch(addService({money: 100, time:10}))
    } else dispatch(removeService({money: 100, time:10}))
  };

  return (
    <button ref={knopka} onClick={click} className={isActive ? 'button active' : 'button'}>
      {isActive ? "remove" : "add"}
    </button>
  );
};

export default AddButton;
